// Modal CSS
.modal-header {
  padding: 15px 45px 15px 15px;
}

// Responsive CSS
@media only screen and (max-width: 767px) {
  .card-body .card-sku {
    text-align: left;
  }
  nav .navUser-item--cart .navUser-action svg {
    width: 24px !important;
    height: 24px !important;
  }
  .countPill {
    box-shadow: none !important;
  }
}

@media only screen and (max-width: 666px) {
  .countPill {
    right: 8px;
    bottom: 2px;
  }
}

@media only screen and (max-width: 425px) {
  .alertBox-message span {
    word-break: break-all;
  }
}

// Forget Password Page 
.forgot-password-form .form-label {
  text-align: center;
}

.forgot-password-form .form-prefixPostfix .form-input {
  margin: 0 auto;
}

.forgot-password-form .form-prefixPostfix .g-recaptcha > div {
  margin-left: auto !important;
  margin-right: auto !important;
}

.type-forgotpassword .forgot-password-form .form-prefixPostfix .button {
  margin-left: auto;
  margin-right: auto;
}

#bwp-co {
  @media screen and (min-width: 1023px) {
    max-width: 458px;
  }
}

.bv_histogram_row_prefix {
  position: relative;
  left: -5px;
  top: -2px;
}

@media screen and (max-width: 740px) {
  .add-to-cart-wrapper {
    width: calc(100% - 130px);

    .form-action {
      width: 100%;
    }
  }
}

.type-category #product-listing-container .pagination {
  margin-bottom: 30px;
}

.faqs h4 {
  cursor: pointer;
  position: relative;
  padding-right: 20px;
}

.faqs h4:after {
  content: "+";
  display: inline-block;
  position: absolute;
  top: -2px;
  right: 0;
}

.faqs h4.active:after {
  content: "-";
  display: inline-block;
}

.faqs p, .faqs ul, .faqs > div{
  display: none;
}

.faqs h4.active + div > *{
    display: block;
}

.upc-label, [data-product-upc] {
  display: none !important;
}

@media screen and (max-width: 1024px) {
    .oc-catgory-image {
        display: none;
    }
}

.pre-header__text--mobile span{
    text-transform: uppercase;
}
@media (max-width: 768px){
    .pre-header__text--mobile span{
        font-size: 14px;
    }
}
.free-shipping-msg > div{
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
    text-align: center;
    text-transform: uppercase;
    color: #114b9c;
}
.free-shipping-msg > div span:first-child{
    font-size: 22px;
    font-weight: 600;
}

.pfirst, .plast {
  svg {
    width: 7px !important;
  }
}

.card-body .product-option-type ul li .form-option-variant--color, .card-body .product-option-type ul li .form-option-variant--pattern {
  border: 1px solid black;
}

.sidebarBlock .navList-item.collapsable > .navList-action {
    font-weight: 600;
}

.sticky-header {
  nav .navUser-action {
    padding: 15px 0;
  }
  nav .compare-header .countPill {
    bottom: 10px;
  }
  .countPill {
    bottom: 7px;
  }
  .pre-header .container-lg {
    padding-top: 10px;
    padding-bottom: 10px;
  }
  .nav-desktop-only .container-lg .navPages-container .navPages > ul li > .navPages-action {
    padding: 10px 0;
  }
}
.productView-images .productView-thumbnails {
  margin-left: 2px;
  .productView-thumbnail {
    width: 14.2%;
  }
}

.stock-info {
  display: none;
}
.sub-category-wrap, #faceted-search-container .oc-catgory-image{
    display: none;
}

