/***** Homepage edits *****/
.category-content{
    padding: 35px 0;
    h3{
        margin-bottom: 7px;
        a{
            color: #333;
        }
    }
    .button{
        background-color: transparent;
        &:hover{
            background-color: #333;
        }
    }
    .btn-wrap.center{
        text-align: center;
    }
}
.custom-content{
    padding: 20px 15px;
    .home-featured-prdts-title{
        text-align: left;
        margin-bottom: 20px;
        line-height: 1.2;
        strong{
            display: block;
            line-height: 1.15;
        }
    }
}
.custom-content .home-featured-prdts-title.title strong{
    display: inline-block;
}
[data-layout-name="Shop By Sink Style"] > div{
  flex-basis: 50%;
    max-width: 50%;
    img{
        width: 100% !important;
    }
}
.features-block{
    background-color: #114b9c;
    color: #fff;
    text-align: center;
    padding: 56px 0;
    div{
        height: 70px;
        display: flex;
        justify-content: center;
        align-items: center;
    }
    ul{
        margin: 0;
        display: flex;
        flex-wrap: wrap;
        align-items: flex-start;
    }
    li{
        padding: 20px !important;
        width: 50%;
        margin: 0 !important;
    }
    h3{
        margin: 18px 0 0 0;
        font-size: 18px;
        color: #fff;
    }
}
.main-blog-content{
    position: relative;
    & > a{
        position: absolute;
        right: 0;
        top: 42px;
    }
    .custom-content{
        padding: 20px 0;
        p{
            line-height: 23px !important;
            font-size: 1rem !important;
        }
        .title{
            padding-right: 60px;
        }
    }
}
#faq-main{
    max-width: 930px;
    margin: 0 auto;
    padding-bottom: 10px;
    &.toggle-main .toggle .toggle-title h3{
        background-color: transparent;
        padding: 15px 45px 15px 0;
        font-size: 16px;
        font-weight: 700;
        color: #333;
        border-radius: 0;
        &:after{
            content: "+";
            display: inline-block;
            position: absolute;
            top: 13px;
            right: 0;
            color: #114b9c;
            font-size: 30px;
            font-weight: 400;
        }
    }
    &.toggle-main .toggle.current .toggle-title h3:after{
        content: "-";
        font-size: 60px;
    }
    &.toggle-main .toggle-content{
        padding: 0;
        background-color: transparent;
        p:last-child{
            margin-bottom: 24px;
        }
    }
    &.toggle-main .toggle:last-child{
        border-bottom: 1px dotted #a3a4a7;
    }
}
.review-slider.slick-slider{
    padding-bottom: 30px;
    margin-bottom: 90px;
    .slick-slide{
        padding: 0 10px;
    }
    p{
        margin-bottom: 10px;
    }
    .author{
        font-weight: 700;
    }
    .review-rating span{
        display: inline-block;
        width: 120px;
        height: 20px;
        background: url("https://cdn11.bigcommerce.com/s-ud2xpqoos0/images/stencil/original/image-manager/rating.png") no-repeat center;
    }
    .slick-dots{
        margin-bottom: 0;
        li{
            margin: 0 5px;
            padding: 0;
            width: 15px;
            height: 15px;
            button{
                border: none;
                background-color: #c5d4e9;
                &:hover:before{
                    background-color: transparent;
                }
                &:focus{
                    outline: none !important;
                }
            }
            &.slick-active button{
                background-color: #114b9c;
                &:before{
                    background-color: transparent;
                }
                &:focus{
                    outline: none !important;
                }
            }
        }
    }
}
@media (min-width: 768px){
[data-layout-name="Shop By Sink Style"] > div{
  flex-basis: 33.33%;
    max-width: 33.33%;
}
.features-block ul li{
    width: 33.33%;
}
}
@media (min-width: 992px){
    .custom-content{
        padding: 2% 11%;
    }
    [data-layout-name="Shop By Sink Style"] > div{
      flex-basis: 20%;
        max-width: 20%;
    }
    .features-block ul li{
        width: 20%;
    }
}
@media (max-width: 992px){
    [data-content-region=home_below_carousel] .custom-html p{
        font-size: 1rem;
    }
}
/***** Homepage edits *****/

/***** Banner edits ******/
.heroCarousel-content .button{
    font-size: 16px;
    background: #2b3990;
    color: #fff;
    font-weight: 500;
    border: none;
    padding: 10px 15px 9px;
}
@media (min-width: 666.98px){
    .heroCarousel-slide .heroCarousel-content {
        padding: 2rem 2.5rem;
        width: 565px;
        left: 0;
        max-width: 100%;
        right: initial;
        top: initial;
        transform: none;
        bottom: 50px;
        left: 40px;
    }
}
@media (min-width: 768px){
    .heroCarousel-slide .heroCarousel-content {
        width: 675px;
    }
    .heroCarousel-content .heroCarousel-title{
        font-size: 45px;
        color: #202020;
        line-height: 1.2;
        font-weight: 700;
        margin-bottom: 10px;
    }
    .heroCarousel-content .heroCarousel-description {
        font-size: 29px;
        line-height: 1.2;
        margin-bottom: 15px;
    }
    .heroCarousel-content .button{
        font-size: 24px;
        padding: 13px 20px 12px;
    }
}
/***** Banner edits ******/

