/*** Home featured section ***/
.featured-section{
    text-align: center;
    padding: 105px 0;
    .content{
        width: 610px;
        margin: 0 auto;
        max-width: 100%;
        padding: 0 20px;
    }
    h2{
        font-size: 34px;
        font-weight: 700;
        color: #fff;
        letter-spacing: 0;
        line-height: 1;
        margin: 40px 0;
    }
    .button{
        font-size: 18px;
        color: #fff;
        background-color: transparent;
        border-color: #fff;
        padding: 12px 30px 11px;
        &:hover{
            color: #000;
            background-color: #fff;
        }
    }
}
@media (min-width: 768px){
.featured-section h2{
    font-size: 52px;
}    
}
