/**** Footer edits start ****/
@media (min-width: 1024px){
.footer .footer-top .footer-pages-list li{
    margin-top: 0 !important;
    margin-bottom: 14px !important;
} 
.footer .footer-top .footer-pages-list:before{
    content: "";
    clear: both;
}
.footer .footer-top .footer-pages-list li{
    float: left;
    width: 50%;
}
.footer .footer-top .custom-row .grid{
    width: 22%;
}
.footer .footer-top .custom-row .grid:nth-child(2){
    width: 34%;
}
}
@media (min-width: 1581px){
.footer .footer-top .footer-pages-list li{
    margin-bottom: 19px !important;
}    
}
/**** Footer edits end ****/

