components/1center-custom/oc-subcategori:
{{#if category}}
  {{#if category.subcategories}}
    <div class="sub-category-wrap clearfix">
      <h3>Sub Categories</h3>
        <ul class="sub-category-block custom-row clearfix align-center">
          {{#each category.subcategories}}
          <li class="grid sub-category-item clearfix">
              <div class="category-image-inner scale-img">
                <a class="sub-category-img-block" href="{{url}}" alt="{{name}}" title="{{name}}">
                  <div class="sub-category-img ">
                  {{#if image}}
                    <img class="lazyload" data-sizes="auto" src="{{getImage image 'sub-category-img'}}" data-src="{{getImage image 'sub-category-img'}}" alt="" width="250" height="250" loading="lazy"/>
                  {{else}}
                    <img class="sub-no-img" src="{{cdn 'assets/img/noImage.jpg'}}" alt="No Image" title="No Image" width="250" height="250" loading="lazy">
                  {{/if}}
                  </div>
                </a>
                <p class="sub-category-name">
                  <a href="{{url}}">{{name}}</a>
                </p>
              </div>
          </li>
          {{/each}}
        </ul>
    </div>
    {{/if}}
{{/if}}
