pages/product:
---
product:
    videos:
        limit: {{theme_settings.productpage_videos_count}}
    reviews:
        limit: {{theme_settings.productpage_reviews_count}}
    related_products:
        limit: {{theme_settings.productpage_related_products_count}}
    similar_by_views:
        limit: {{theme_settings.productpage_similar_by_views_count}}
---
{{inject 'productId' product.id}}

{{#partial "page"}}
    <div class="page-inner">
    {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}

    {{#each product.shipping_messages}}
        {{> components/common/alert/alert-info message}}
    {{/each}}

    <div>
        {{> components/products/product-view}}

        {{{region name="product_below_content"}}}
      
        <div class="oc-tabing-section mt-120">
            <article class="productView-description">
                    {{#if theme_settings.show_product_details_tabs}}
                        {{> components/1center-custom/oc-description-tabs}}
                    {{else}}
                        {{> components/1center-custom/oc-description}}
                    {{/if}}
                <div
                        data-bv-show="reviews"
                        data-bv-product-id="{{product.sku}}">
                </div>
                    {{#all settings.show_product_reviews theme_settings.show_product_reviews (if theme_settings.show_product_details_tabs '!==' true)}}
                        {{> components/products/reviews reviews=product.reviews product=product urls=urls}}
                    {{/all}}
        
            </article>
            <div class="oc-related-product">
                {{> components/1center-custom/oc-realted-product}}
            </div>
        </div>
        
       
    </div>
    {{{region name="product_above_customer_also_view"}}}

    {{> components/products/schema}}
</div>
{{/partial}}
{{#partial "page-full"}}
    <div class="oc-customer-also-view">
    {{> components/products/tabs}}
    </div>
{{/partial}}
{{> layout/base}}

<script type="text/javascript">
var _learnq = _learnq || [];

var product_categories = [];
{{#each product.category}}
product_categories.push('{{this}}')
{{/each}}

var item = {
Name: "{{product.title}}",
ProductID: "{{product.id}}",
ImageURL: "{{getImage product.main_image 'product_size' (cdn theme_settings.default_image_product)}}",
URL: "{{product.url}}",
Brand: "{{product.brand.name}}",
Categories: product_categories,
Price: "{{product.price.without_tax.value}}",
CompareAtPrice:"{{product.price.rrp_without_tax.value}}"
};

_learnq.push(['track', 'Viewed Product', item]);

_learnq.push(['trackViewedItem', {
Title: item.Name,
ItemId: item.ProductID,
Categories: item.Categories,
ImageUrl: item.ImageURL,
Url: item.URL,
Metadata: {
Brand: item.Brand,
Price: item.Price,
CompareAtPrice: item.CompareAtPrice
}
}]);
</script>
